$_MODULE['<{shoppingfeed}prestashop>faq_12fb11d91fe23e9a4fb6e1a19d053c80'] = 'Le module 15 min Marketplace Updates - Shopping Feed fonctionne-t-il avec le module Shopping Flux Officiel ?';
